// src/App.js
import React, { useState, useEffect } from 'react';
import { Button, Container, CssBaseline, Typography } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import './App.css';

const emotionsData = [
  { emotion: "Happy", emoji: "ðŸ˜Š", imageUrl: "https://example.com/happy-image.jpg" },
  { emotion: "Sad", emoji: "ðŸ˜¢", imageUrl: "https://example.com/sad-image.jpg" },
  { emotion: "Angry", emoji: "ðŸ˜ ", imageUrl: "https://example.com/angry-image.jpg" },
  // Add data for other emotions
];

const theme = createTheme();

const App = () => {
  const [currentEmotionData, setCurrentEmotionData] = useState({});
  const [options, setOptions] = useState([]);
  const [transitionClass, setTransitionClass] = useState('');

  useEffect(() => {
    startGame();
  }, []); // Run once on component mount

  const startGame = () => {
    // Add 'exit' class to trigger the exit animation
    setTransitionClass('exit');

    // Wait for the exit animation to complete before updating state
    setTimeout(() => {
      // Choose a random emotion data
      const newEmotionData = emotionsData[Math.floor(Math.random() * emotionsData.length)];
      setCurrentEmotionData(newEmotionData);

      // Generate two random emotions for options
      const newOptions = generateRandomOptions(newEmotionData.emotion);
      setOptions(newOptions);

      // Add 'enter' class to trigger the enter animation
      setTransitionClass('enter');
    }, 300); // The duration of the exit transition (in milliseconds)
  };

  const generateRandomOptions = (correctEmotion) => {
    const newOptions = [];

    while (newOptions.length < 2) {
      const randomEmotionData = emotionsData[Math.floor(Math.random() * emotionsData.length)];
      if (randomEmotionData.emotion !== correctEmotion && !newOptions.some(option => option.emotion === randomEmotionData.emotion)) {
        newOptions.push(randomEmotionData);
      }
    }

    newOptions.push({ emotion: correctEmotion, emoji: currentEmotionData.emoji, imageUrl: currentEmotionData.imageUrl });
    return shuffleArray(newOptions);
  };

  const shuffleArray = (array) => {
    // Fisher-Yates shuffle algorithm
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  };

  const checkAnswer = (selectedEmotion) => {
    if (selectedEmotion.emotion === currentEmotionData.emotion) {
      alert("Correct! Let's try another one.");
      startGame();
    } else {
      alert("Incorrect. Try again!");
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container component="main" maxWidth="xs">
        <div className={`paper ${transitionClass}`}>
          <Typography component="h1" variant="h5" id="game-title">
            Word Game
          </Typography>
          <div id="options" className={transitionClass}>
            {options.map((option, index) => (
              <div key={index} className={transitionClass}>
                <Typography variant="body2" align="center">
                  {option.emotion}
                </Typography>
                <Button
                  variant="contained"
                  color="success"
                  onClick={() => checkAnswer(option)}
                  fullWidth
                >
                  {option.emoji}
                </Button>
                <img
                  src={option.imageUrl}
                  alt={option.emotion}
                  style={{ width: '100%', marginTop: '10px' }}
                />
              </div>
            ))}
          </div>
        </div>
      </Container>
    </ThemeProvider>
  );
};

export default App;
